// Loop over input items and add a new field called 'myNewField' to the JSON of each one
const markdown = $('Edit Fields5').first().json.sowsheet_markdown;

// Very simple Markdown to HTML (not fully styled, for quick prototyping)
const html = `
  <html>
    <head><meta charset="utf-8"><style>
      body { font-family: sans-serif; padding: 2rem; }
      table { border-collapse: collapse; }
      th, td { border: 1px solid #999; padding: 0.5rem; }
    </style></head>
    <body>${markdown.replace(/\n/g, "<br>")}</body>
  </html>
`;

return [{
  json: {
    html
  }
}];
